mapper.select:
select *
from(
	select rownum rnum, no, title, writer, writeDate, fileName
	from(
		select no, title, writer, writeDate, fileName
		from image
		
		order by no desc
	)
)
where rnum between #{startRow} and #{endRow}

select no, title, content, writer, writeDate, fileName
from image
where no = #{no}

mapper.update:
update image
set title = #{title}, content = #{content}, writer = #{writer}
where no = #{no}

update image
set fileName = #{fileName}
where no = #{no}
